import React, { useState, useEffect } from 'react'; // ðŸ‘ˆ Ajout de useEffect
import axios from 'axios'; // ðŸ‘ˆ Ajout de axios
import { logout } from '../services/authService';
import { useNavigate, Link } from 'react-router-dom';
import './EntrepriseDashboard.css';

const EntrepriseDashboard = () => {
  const [photo, setPhoto] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const saved = localStorage.getItem("entreprisePhoto");
    if (saved) {
      setPhoto(saved);
    } else {
      axios.get("http://localhost:5000/api/entreprises/profil", {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      })
      .then(res => {
        if (res.data.photo) {
          setPhoto(`http://localhost:5000/uploads/${res.data.photo}`);
        } else {
          setPhoto('/default-avatar.png');
        }
      })
      .catch(err => console.error("Erreur chargement photo :", err));
    }
  }, []);

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const handleProfil = () => {
    navigate('/entreprise/profil');
  };

  const posts = [
    {
      auteur: "Etudiant Â· Oussama",
      contenu: "Je cherche un stage en dÃ©veloppement web pour cet Ã©tÃ©.",
      date: "Il y a 2 jours"
    },
    {
      auteur: "Entreprise Â· TechSolutions",
      contenu: "Nous recrutons un stagiaire en data science pour 3 mois. Envoyez-nous vos CVs !",
      date: "Il y a 1 jour"
    }
  ];

  return (
    <div className="dashboard-container">
      <nav className="navbar">
        {/* Ã€ gauche */}
        <div className="navbar-logo" onClick={() => navigate('/entreprise')}>
          Intern'<span style={{ color: '#1C274C' }}>Net</span>
        </div>

        {/* Ã€ droite */}
        <div className="navbar-actions">
          <img
            src={photo || '/default-avatar.png'}
            alt="Profil"
            className="navbar-avatar"
            onClick={handleProfil}
          />
          <Link to="/mes-stages" className="nav-button">Mes Offres</Link>
          <button onClick={handleLogout}>DÃ©connexion</button>
        </div>
      </nav>


      <div className="main-content">
        <div className="feed">
          <h3>Fil dâ€™actualitÃ©</h3>
          {posts.map((post, index) => (
            <div className="post" key={index}>
              <strong>{post.auteur}</strong>
              <p>{post.contenu}</p>
              <small>{post.date}</small>
            </div>
          ))}
        </div>

        <div className="sidebar">
          <h4>Suggestions</h4>
          <p>Ajoutez un nouveau stage pour attirer des talents !</p>
          <button className="btn-ajouter" onClick={() => navigate('/creer-stage')}>Publier une offre</button>
        </div>
      </div>
    </div>
  );
};

export default EntrepriseDashboard;
