import React, { useState, useEffect } from 'react';
import './EtudiantDashboard.css';
import { logout } from '../services/authService';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const EtudiantDashboard = () => {
  const [offers, setOffers] = useState([]);
  const [selectedOffer, setSelectedOffer] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({ motivation: '', cvLink: '' });

  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  useEffect(() => {
    axios.get("http://localhost:5000/api/stages")
      .then(res => setOffers(res.data))
      .catch(err => console.error("Erreur chargement des stages", err));
  }, []);

  const handleInputChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handlePostuler = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.post(`http://localhost:5000/api/candidatures`, {
        stageId: selectedOffer.id,
        motivation: formData.motivation,
        cvLink: formData.cvLink,
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert("Candidature envoyÃ©e avec succÃ¨s !");
      setShowForm(false);
      setFormData({ motivation: '', cvLink: '' });
    } catch (err) {
      console.error("Erreur postulation :", err);
      alert("Erreur lors de l'envoi de la candidature.");
    }
  };

  return (
    <div className="etudiant-dashboard">
      <nav className="navbar">
        <h1>ðŸŽ“ OpportunitÃ©s de Stage</h1>
        <button onClick={handleLogout}>Se dÃ©connecter</button>
      </nav>

      <div className="dashboard-main">
        <aside className="offre-liste">
          <h2>ðŸ“‹ Liste des offres</h2>
          {offers.map((offer, index) => (
            <div
              key={index}
              className={`offre-item ${selectedOffer === offer ? 'active' : ''}`}
              onClick={() => {
                setSelectedOffer(offer);
                setShowForm(false);
              }}
            >
              <h3>{offer.titre}</h3>
              <p><strong>Entreprise:</strong> {offer.entreprise?.nomSociete || 'N/A'}</p>
              <p><strong>Lieu:</strong> {offer.lieu}</p>
              <p><strong>Domaine:</strong> {offer.domaine}</p>
              <p><strong>PÃ©riode:</strong> {offer.dateDebut?.slice(0,10)} - {offer.dateFin?.slice(0,10)}</p>
            </div>
          ))}
        </aside>

        <section className="details-panel">
          {selectedOffer ? (
            <div>
              <h2>{selectedOffer.titre}</h2>
              <h4 className="entreprise">{selectedOffer.entreprise?.nomSociete}</h4>
              <p><strong>Lieu:</strong> {selectedOffer.lieu}</p>
              <p><strong>Domaine:</strong> {selectedOffer.domaine}</p>
              <p><strong>Dates:</strong> {selectedOffer.dateDebut?.slice(0,10)} - {selectedOffer.dateFin?.slice(0,10)}</p>
              <hr />
              <h3>Description du stage</h3>
              <p>{selectedOffer.description || "Aucune description fournie."}</p>
              <button className="btn-postuler" onClick={() => setShowForm(true)}>ðŸ“¨ Postuler</button>

              {showForm && (
                <form className="form-postulation" onSubmit={handlePostuler}>
                  <h4>Formulaire de candidature</h4>
                  <textarea name="motivation" placeholder="Votre motivation..." required value={formData.motivation} onChange={handleInputChange} />
                  <input type="url" name="cvLink" placeholder="Lien vers votre CV (Google Drive, etc.)" required value={formData.cvLink} onChange={handleInputChange} />
                  <button type="submit">Envoyer</button>
                </form>
              )}
            </div>
          ) : (
            <div className="no-selection">
              <p>SÃ©lectionnez une offre pour voir les dÃ©tails.</p>
            </div>
          )}
        </section>
      </div>
    </div>
  );
};

export default EtudiantDashboard;
