import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './EntrepriseProfile.css';

const EntrepriseProfile = () => {
  const [profil, setProfil] = useState(null);

  useEffect(() => {
    const fetchProfil = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('http://localhost:5000/api/entreprises/profil', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setProfil(res.data);
      } catch (err) {
        console.error("Erreur lors du chargement du profil :", err);
      }
    };

    fetchProfil();
  }, []);

  if (!profil) return <p>Chargement du profil...</p>;

  return (
    <div className="entreprise-profile">
      <h2>Mon Profil Entreprise</h2>
      <div className="profile-section">
        <p><strong>Nom Société :</strong> {profil.nomSociete}</p>
        <p><strong>Secteur :</strong> {profil.secteur}</p>
        <p><strong>Adresse :</strong> {profil.adresse}</p>
        <p><strong>Email RH :</strong> {profil.hr_email}</p>
        <p><strong>Téléphone :</strong> {profil.telephone || 'Non renseigné'}</p>
        <p><strong>Site Web :</strong> {profil.siteWeb || 'Non renseigné'}</p>
        <p><strong>Description :</strong> {profil.description || 'Non renseignée'}</p>
      </div>
    </div>
  );
};

export default EntrepriseProfile;
