import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { register } from '../services/authService';

const Register = () => {
  const [formData, setFormData] = useState({
    nom: '',
    prenom: '',
    email: '',
    motdepasse: '',
    role: 'etudiant', // valeur par défaut
    niveau: '',
    filiere: '',
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await register(formData);
      alert('Inscription réussie');
      navigate('/login');
    } catch (err) {
      console.error('Erreur lors de l’inscription :', err);
      alert('Erreur d’inscription !');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" name="nom" placeholder="Nom" onChange={handleChange} />
      <input type="text" name="prenom" placeholder="Prénom" onChange={handleChange} />
      <input type="email" name="email" placeholder="Email" onChange={handleChange} />
      <input type="password" name="motdepasse" placeholder="Mot de passe" onChange={handleChange} />

      <select name="role" onChange={handleChange}>
        <option value="etudiant">Étudiant</option>
        <option value="entreprise">Entreprise</option>
      </select>

      {formData.role === 'etudiant' && (
        <>
          <input type="text" name="niveau" placeholder="Niveau" onChange={handleChange} />
          <input type="text" name="filiere" placeholder="Filière" onChange={handleChange} />
        </>
      )}

      <button type="submit">S'inscrire</button>
    </form>
  );
};

export default Register;
