      ) : (
        <ul>
          {candidatures.map((c) => (
            <li key={c.id}>
              <ul>
            {candidatures.map((c, index) => (
                <li key={index}>
                {c.etudiant?.prenom} {c.etudiant?.nom}
                </li>
            ))}
            </ul>

            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default CandidaturesStage;
