import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import './AdminStagesList.css';

const AdminStagesList = () => {
  const { statut } = useParams();
  const [stages, setStages] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchStages = async () => {
      const token = localStorage.getItem('token');
      try {
        const res = await axios.get(`http://localhost:5000/api/stages`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        const filtered = res.data.filter(s => s.status === statut);
        setStages(filtered);
      } catch (err) {
        console.error('Erreur lors de la récupération des stages :', err);
      }
    };

    fetchStages();
  }, [statut]);

  const capitalize = (s) => s.charAt(0).toUpperCase() + s.slice(1);

  return (
    <div className="admin-stages-list">
      <div className="header">
        <button onClick={() => navigate('/admin')} className="back-btn">⬅ Retour</button>
        <h2>Stages {capitalize(statut)}</h2>
      </div>

      {stages.length === 0 ? (
        <p>Aucun stage {statut} pour le moment.</p>
      ) : (
        <div className="stage-cards">
          {stages.map(stage => (
            <div className="stage-card" key={stage.id}>
              <h3>{stage.titre}</h3>
              <p><strong>Entreprise :</strong> {stage.entreprise?.nomSociete || 'N/A'}</p>
              <p><strong>Lieu :</strong> {stage.lieu}</p>
              <p><strong>Domaine :</strong> {stage.domaine}</p>
              <p><strong>Période :</strong> {stage.dateDebut?.slice(0, 10)} - {stage.dateFin?.slice(0, 10)}</p>
              <p><strong>Description :</strong> {stage.description || 'Aucune description'}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default AdminStagesList;
