import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { login } from '../services/authService';
import './Login.css';
import illustration from '../assets/internship-illustration.png'; // remplace par ton vrai chemin

const Login = () => {
  const [credentials, setCredentials] = useState({
    user_email: '',
    user_password: ''
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const data = await login(credentials.user_email, credentials.user_password);
      const role = data.user.role;
      if (role === "etudiant") navigate("/etudiant");
      else if (role === "entreprise") navigate("/entreprise");
      else if (role === "admin") navigate("/admin");
    } catch (err) {
      console.error("Erreur de connexion :", err.response?.data || err.message);
      alert("Email ou mot de passe incorrect !");
    }
  };

  return (
    <div className="login-page">
      <div className="login-box">
        <div className="login-form-section">
          <h2>Connexion</h2>
          <form onSubmit={handleSubmit} autoComplete="off">
            <input
              type="email"
              name="user_email"
              placeholder="Email"
              value={credentials.user_email}
              onChange={handleChange}
              required
            />
            <input
              type="password"
              name="user_password"
              placeholder="Mot de passe"
              value={credentials.user_password}
              onChange={handleChange}
              required
            />
            <button type="submit">Se connecter</button>
          </form>
          <p className="register-link">
            Pas de compte ? <Link to="/register">Cr√©er un compte</Link>
          </p>
        </div>
        <div className="login-illustration">
          <img src={illustration} alt="illustration de connexion" />
        </div>
      </div>
    </div>
  );
};

export default Login;
