import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './EntrepriseProfile.css';

const EntrepriseProfile = () => {
  const [profil, setProfil] = useState(null);
  const [stages, setStages] = useState([]);
  const [candidatures, setCandidatures] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');

    const fetchProfil = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/entreprises/profil', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setProfil(res.data);
      } catch (err) {
        console.error("Erreur profil :", err);
      }
    };

    const fetchStages = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/stages/mine', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setStages(res.data);
      } catch (err) {
        console.error("Erreur stages :", err);
      }
    };

    const fetchCandidatures = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/entreprises/candidatures', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setCandidatures(res.data);
      } catch (err) {
        console.error("Erreur candidatures :", err);
      }
    };

    fetchProfil();
    fetchStages();
    fetchCandidatures();
  }, []);

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!selectedFile) return;
    const token = localStorage.getItem('token');
    const formData = new FormData();
    formData.append('photo', selectedFile);

    try {
      await axios.patch('http://localhost:5000/api/entreprises/photo', formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'multipart/form-data'
        }
      });
      alert("Photo mise à jour !");
      window.location.reload();
    } catch (err) {
      console.error("Erreur upload photo :", err);
    }
  };

  if (!profil) return <p>Chargement du profil...</p>;

  return (
    <div className="entreprise-profile">
      <h2>Mon Profil Entreprise</h2>

      <div className="profile-header">
        <img
          src={profil.photo ? `http://localhost:5000/uploads/${profil.photo}` : '/default-avatar.png'}
          alt="Profil"
          className="profile-photo"
        />
        <input type="file" onChange={handleFileChange} />
        <button onClick={handleUpload}>Mettre à jour la photo</button>
      </div>

      <div className="profile-info">
        <p><strong>Nom Société :</strong> {profil.nomSociete}</p>
        <p><strong>Secteur :</strong> {profil.secteur}</p>
        <p><strong>Adresse :</strong> {profil.adresse}</p>
        <p><strong>Email RH :</strong> {profil.hr_email}</p>
        <p><strong>Téléphone :</strong> {profil.telephone || 'Non renseigné'}</p>
        <p><strong>Site Web :</strong> {profil.siteWeb || 'Non renseigné'}</p>
        <p><strong>Description :</strong> {profil.description || 'Non renseignée'}</p>
      </div>

      <hr />

      <h3>Mes Offres de Stage</h3>
      <div className="stage-list">
        {stages.map(stage => (
          <div className="stage-card" key={stage.id}>
            <h4>{stage.titre}</h4>
            <p><strong>Domaine:</strong> {stage.domaine}</p>
            <p><strong>Lieu:</strong> {stage.lieu}</p>
            <p><strong>Période:</strong> {stage.dateDebut?.slice(0, 10)} - {stage.dateFin?.slice(0, 10)}</p>
          </div>
        ))}
      </div>

      <hr />

      <h3>Candidatures reçues</h3>
      <div className="candidature-list">
        {candidatures.length === 0 ? (
          <p>Aucune candidature pour l’instant.</p>
        ) : (
          candidatures.map(c => (
            <div className="candidature-card" key={c.id}>
              <p><strong>Étudiant :</strong> {c.etudiant?.prenom} {c.etudiant?.nom}</p>
              <p><strong>Filière :</strong> {c.etudiant?.filiere}</p>
              <p><strong>Niveau :</strong> {c.etudiant?.niveau}</p>
              <p><strong>Status :</strong> {c.status}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default EntrepriseProfile;
