import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './CandidaturesStage.css'; // üëà importe le style

const CandidaturesStage = () => {
  const { id } = useParams();
  const [candidatures, setCandidatures] = useState([]);

  const fetchCandidatures = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get(`http://localhost:5000/api/candidatures/stage/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setCandidatures(res.data);
    } catch (err) {
      console.error("Erreur chargement candidatures :", err);
    }
  };

  useEffect(() => {
    fetchCandidatures();
  }, [id]);

  const updateStatus = async (candidatureId, status) => {
    try {
      const token = localStorage.getItem('token');
      await axios.patch(`http://localhost:5000/api/candidatures/${candidatureId}/status`, {
        statut: status
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchCandidatures();
    } catch (err) {
      console.error("Erreur mise √† jour du statut :", err);
    }
  };

  return (
    <div className="candidature-page">
      <h2>üì• Candidatures pour ce stage</h2>
      {candidatures.length === 0 ? (
        <p>Aucune candidature re√ßue.</p>
      ) : (
        candidatures.map((c) => (
          <div key={c.id} className="candidature-card">
            <p><strong>Lettre de motivation :</strong><br />{c.message || <em>(Non renseign√©e)</em>}</p>
            <p><strong>CV :</strong> {c.cv ? (
              <a href={`http://localhost:5000/uploads/${c.cv}`} target="_blank" rel="noreferrer">Voir le CV</a>
            ) : "Non fourni"}</p>
            <p><strong>Statut :</strong> <span className={`candidature-status ${c.statut}`}>{c.statut}</span></p>

            <div className="candidature-actions">
              <button className="accept" onClick={() => updateStatus(c.id, 'accept√©e')}>‚úÖ Accepter</button>
              <button className="refuse" onClick={() => updateStatus(c.id, 'refus√©e')}>‚ùå Refuser</button>
            </div>
          </div>
        ))
      )}
    </div>
  );
};

export default CandidaturesStage;
