import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const CandidaturesStage = () => {
  const { id } = useParams();
  const [candidatures, setCandidatures] = useState([]);

  useEffect(() => {
    const fetchCandidatures = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get(`http://localhost:5000/api/candidatures/stage/${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        console.log("Candidatures re√ßues :", res.data);
        setCandidatures(res.data);
      } catch (err) {
        console.error("Erreur chargement candidatures :", err);
      }
    };

    fetchCandidatures();
  }, [id]);

  return (
    <div style={{ padding: '40px', fontFamily: 'Segoe UI, sans-serif' }}>
      <h2>üì• Candidatures pour ce stage</h2>
      {candidatures.length === 0 ? (
        <p>Aucune candidature re√ßue.</p>
      ) : (
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {candidatures.map((c) => (
            <li key={c.id} style={{
              backgroundColor: '#fff',
              marginBottom: '20px',
              padding: '20px',
              borderRadius: '10px',
              boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
              borderLeft: '5px solid #2f486d'
            }}>
              <h4>{c.etudiant?.prenom} {c.etudiant?.nom}</h4>
              <p><strong>Fili√®re :</strong> {c.etudiant?.filiere}</p>
              <p><strong>Niveau :</strong> {c.etudiant?.niveau}</p>
              <p><strong>Statut :</strong> {c.statut}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default CandidaturesStage;
