import React, { useState } from 'react';
import axios from 'axios';
import './CreateStage.css';

const CreateStage = ({ token }) => {
  const [form, setForm] = useState({
    titre: '',
    description: '',
    domaine: '',
    lieu: '',
    dateDebut: '',
    dateFin: '',
  });

  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/stages', form, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setMessage('Stage créé avec succès !');
      setForm({
        titre: '',
        description: '',
        domaine: '',
        lieu: '',
        dateDebut: '',
        dateFin: '',
      });
    } catch (err) {
      console.error(err);
      setMessage('Erreur lors de la création du stage.');
    }
  };

  return (
    <div className="create-stage-container">
      <h2>Publier une offre de stage</h2>
      {message && <p className="message">{message}</p>}
      <form className="stage-form" onSubmit={handleSubmit}>
        <input name="titre" placeholder="Titre" value={form.titre} onChange={handleChange} required />
        <input name="domaine" placeholder="Domaine" value={form.domaine} onChange={handleChange} required />
        <input name="lieu" placeholder="Lieu" value={form.lieu} onChange={handleChange} required />
        <textarea name="description" placeholder="Description" value={form.description} onChange={handleChange} rows="5" required />
        <label>Date de début</label>
        <input type="date" name="dateDebut" value={form.dateDebut} onChange={handleChange} required />
        <label>Date de fin</label>
        <input type="date" name="dateFin" value={form.dateFin} onChange={handleChange} required />
        <button type="submit">Publier</button>
      </form>
    </div>
  );
};

export default CreateStage;
