import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { login } from '../services/authService';
import './Login.css';
import loginIllustration from '../assets/internship-illustration.png'; // mets une image de stage ici

const Login = () => {
  const [credentials, setCredentials] = useState({
    user_email: '',
    user_password: ''
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const data = await login(credentials.user_email, credentials.user_password);
      const role = data.user.role;
      if (role === "etudiant") navigate("/etudiant");
      else if (role === "entreprise") navigate("/entreprise");
      else if (role === "admin") navigate("/admin");
    } catch (err) {
      alert("Erreur de connexion !");
    }
  };

  return (
    <div className="login-page">
      <div className="login-left">
        <form className="login-box" onSubmit={handleSubmit}>
          <h1>Bienvenue ðŸ‘‹</h1>
          <p className="subtitle">Connecte-toi Ã  ta plateforme de stages</p>

          <label>Email</label>
          <input
            type="email"
            name="user_email"
            value={credentials.user_email}
            onChange={handleChange}
            required
            placeholder="exemple@email.com"
          />

          <label>Mot de passe</label>
          <input
            type="password"
            name="user_password"
            value={credentials.user_password}
            onChange={handleChange}
            required
            placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"
          />

          <button type="submit" className="login-btn">Se connecter</button>

          <p className="register-link">
            Pas encore de compte ? <Link to="/register">Inscription</Link>
          </p>
        </form>
      </div>

      
    </div>
  );
};

export default Login;
