import React, { useEffect, useState } from "react";
import axios from "axios";

const ProfilEtudiant = () => {
  const [etudiant, setEtudiant] = useState(null);
  const [candidatures, setCandidatures] = useState([]);
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem("token");

      try {
        // Récup info étudiant
        const user = JSON.parse(localStorage.getItem("user"));
        const resEtudiant = await axios.get(
          `http://localhost:5000/api/etudiants/${user.id}`,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        setEtudiant(resEtudiant.data);

        // Récup candidatures
        const resCandidatures = await axios.get(
          `http://localhost:5000/api/candidatures`,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        const mesCandidatures = resCandidatures.data.filter(
          (c) => c.etudiantId === resEtudiant.data.id
        );
        setCandidatures(mesCandidatures);

        // Récup posts (optionnel selon ton backend)
        const resPosts = await axios.get(
          `http://localhost:5000/api/posts?etudiantId=${resEtudiant.data.id}`,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        setPosts(resPosts.data);

      } catch (err) {
        console.error(err);
      }
    };

    fetchData();
  }, []);

  if (!etudiant) return <p>Chargement...</p>;

  return (
    <div className="p-4">
      <div className="flex gap-6 items-center mb-4">
        <img
          src="/avatar.png"
          alt="profil"
          className="w-24 h-24 rounded-full border"
        />
        <div>
          <h2 className="text-2xl font-bold">
            {etudiant.prenom} {etudiant.nom}
          </h2>
          <p>{etudiant.filiere} - {etudiant.niveau}</p>
          <p>{etudiant.email}</p>
        </div>
      </div>

      <div className="mb-6">
        <h3 className="text-xl font-semibold mb-2">Mes Candidatures</h3>
        {candidatures.map((c) => (
          <div key={c.id} className="border p-2 mb-2 rounded">
            <p><strong>Stage ID :</strong> {c.stageId}</p>
            <p><strong>Statut :</strong> {c.statut}</p>
            <p><strong>Message :</strong> {c.message}</p>
          </div>
        ))}
      </div>

      <div>
        <h3 className="text-xl font-semibold mb-2">Mes Publications</h3>
        {posts.map((p) => (
          <div key={p.id} className="border p-2 mb-2 rounded">
            <p><strong>Titre :</strong> {p.titre}</p>
            <p>{p.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProfilEtudiant;
