import React, { useEffect, useState } from 'react';
import axios from 'axios';

function ProfilEtudiant() {
  const [etudiant, setEtudiant] = useState(null);
  const [candidatures, setCandidatures] = useState([]);
  const [loading, setLoading] = useState(true);

  const token = localStorage.getItem('token');
  const user = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res1 = await axios.get(`http://localhost:5000/api/etudiants/${user.id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setEtudiant(res1.data);

        const res2 = await axios.get(`http://localhost:5000/api/etudiants/etudiant`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setCandidatures(res2.data);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) return <div>Chargement...</div>;

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">ðŸ‘¤ Profil Ã‰tudiant</h1>

      <div className="bg-white shadow rounded p-4 mb-6">
        <img
          src="/default-avatar.png"
          alt="Avatar"
          className="w-24 h-24 rounded-full mb-4"
        />
        <p><strong>Nom :</strong> {user.nom} {user.prenom}</p>
        <p><strong>Email :</strong> {user.email}</p>
        <p><strong>Niveau :</strong> {etudiant.niveau}</p>
        <p><strong>FiliÃ¨re :</strong> {etudiant.filiere}</p>
      </div>

      <div className="bg-white shadow rounded p-4">
        <h2 className="text-xl font-semibold mb-2">ðŸ“„ Mes Candidatures</h2>
        {candidatures.length === 0 ? (
          <p>Aucune candidature envoyÃ©e.</p>
        ) : (
          <ul className="space-y-2">
            {candidatures.map(c => (
              <li key={c.id} className="border p-2 rounded">
                <p><strong>Stage :</strong> {c.Stage?.titre}</p>
                <p><strong>Entreprise :</strong> {c.Entreprise?.nom}</p>
                <p><strong>Statut :</strong> {c.statut}</p>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

export default ProfilEtudiant;
